-- Function structure for getAuthorityList
-- ----------------------------
DROP FUNCTION IF EXISTS `getAuthorityList`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `getAuthorityList`(rootId varchar(50)) RETURNS varchar(1000) CHARSET utf8 COLLATE utf8_unicode_ci
BEGIN
       DECLARE sTemp VARCHAR(1000);
       DECLARE sTempChd VARCHAR(1000);
   
       SET sTemp = '';
       SET sTempChd =rootId;
   
       WHILE sTempChd is not null DO
         SET sTemp = concat(sTempChd,',',sTemp);
        SELECT group_concat(authority_id) INTO sTempChd FROM homework_authority where FIND_IN_SET(parent_authority_id,sTempChd)>0;
      END WHILE;
      RETURN sTemp;
     END
;;
DELIMITER ;

