-- Table structure for homework_comment
-- ----------------------------
DROP TABLE IF EXISTS `homework_comment`;
CREATE TABLE `homework_comment` (
  `comment_id` varchar(40) COLLATE utf8_unicode_ci NOT NULL,
  `comment_content` varchar(300) COLLATE utf8_unicode_ci DEFAULT NULL,
  `comment_author` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
